package joe;

public class Triangle {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int c = 1;
		for (int i = 1; i <= 1; i++) {
			for (int j = 1; j <= i; j++) {
				for (int k = 1; k <= j; k++) {
					for (int m = 1; m <= k; m++) {
						c++;
					}
				}
			}

		}
		System.out.println(c);
	}

}
